def first_Character_repeated_thrice(str):
    is_first_Character_repeated = False
    if len(str) > 2:
        if str[0] == str[1] == str[2]:
            is_first_Character_repeated = True
    return is_first_Character_repeated


def first_Character_repeated_twice(str):
    is_first_Character_repeated = False
    if len(str) > 1:
        if str[0] == str[1]:
            is_first_Character_repeated = True
    return is_first_Character_repeated


def find_checksum(string_list):
    two_digit_counter = 0
    three_digit_counter = 0
    for sing_string in string_list:
        temp_sorted_string = ""
        two_character_count_set = False
        three_character_count_set = False
        temp_sorted_string = temp_sorted_string.join(sorted(sing_string))
        while (len(temp_sorted_string) > 1 and not(two_character_count_set and three_character_count_set)):
            if first_Character_repeated_thrice(temp_sorted_string):
                temp_sorted_string = temp_sorted_string[3:]
                if (not three_character_count_set):
                    three_digit_counter += 1
                    three_character_count_set = True
            elif first_Character_repeated_twice(temp_sorted_string):
                temp_sorted_string = temp_sorted_string[2:]
                if (not two_character_count_set):
                    two_digit_counter += 1
                    two_character_count_set = True
            else:
                temp_sorted_string = temp_sorted_string[1:]
    return two_digit_counter * three_digit_counter


